ARD=$HOME/arduino-1.8.5; rm -rf /tmp/bla && mkdir /tmp/bla && $ARD/arduino-builder -compile -logger=machine -hardware $ARD/hardware -tools $ARD/tools-builder -tools $ARD/hardware/tools/avr -built-in-libraries $ARD/libraries -libraries $ARD/libraries -fqbn=teensy:avr:teensy31:usb=serial,speed=48,opt=o2std,keys=en-us -ide-version=10805 -build-path /tmp/bla -warnings=null "$(dirname "$0")"/spiflash.ino && kill "$(pidof $ARD/hardware/tools/teensy)" && \
